export default {
  provide() {
    return {
      rwDispatcherProvider: this
    }
  },
  data() {
    return {
      loading: false,
      query: {}, // 通过路由传来的参数
      type: 'add', // 页面类型，add/detail/update
      form: {}, // 默认form对象
      rwDispatcherState: 'write', // 默认dispatcher模式为write
      maniStatus: { // 不同type对应的dispatcher模式
        add: 'write',
        detail: 'read',
        update: 'write'
      }
    }
  },
  created() {
    // 接收传参并判断页面类型
    this.query = this.$route.query
    this.type = this.query.type || 'add'
    this.changeRwStatus(this.maniStatus[this.type])
    switch (this.type) {
      case 'updaaddte':
        this.title = `新建${this.title}` // title由具体detail文件内定义，用以标注页面标题
        break
      case 'detail':
        this.loading = true
        this.title = `${this.title}详情`
        this.initForm()
        break
      case 'update':
        this.loading = true
        this.title = `编辑${this.title}`
        this.initForm()
        break
      default:
        break
    }
  },
  methods: {
    // 返回
    back() {
      if (this.rwDispatcherState === 'write') {
        this.$confirm('退出后将不保存', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          this.goBack() // 在路由内根据定义的activeMenu，判断返回时的上一级路径
        }).catch(action => {
        })
      } else {
        this.goBack()
      }
    },
    // 取消
    cancel() {
      if (this.rwDispatcherState === 'write') {
        this.$confirm('取消后将不保存', '确认不保存', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(() => {
          if (this.type === 'add') {
            this.goBack() // 在路由内根据定义的activeMenu，判断返回时的上一级路径
          } else {
            this.changeRwStatus('read')
          }
        }).catch(action => {
        })
      } else {
        this.goBack() // 在路由内根据定义的activeMenu，判断返回时的上一级路径
      }
    },
    // 改变rw状态
    changeRwStatus(status) {
      this.rwDispatcherState = status
    },
    // 保存
    async save() {
      // form校验，需要匹配form标签的ref="form"
      var flag = true
      this.$refs.form.validate((valid) => {
        flag = valid
      })
      if (!flag) {
        return
      }
      let res
      if (this.type === 'add') {
        res = await this.doSave() // doSave方法写在detail文件内
      } else if (this.type === 'update') {
        res = await this.doUpdate() // doUpdate方法写在detail文件内
      } else if (this.type === 'detail') {
        res = await this.doDetail() // doDetail方法写在detail文件内
      }
      if (res && res.code === '0') {
        this.$message.success('保存成功')
        this.goBack()
      }
    },
    goBack() {
      this.$router.go(-1)
    }
  }
}

export default {
  data() {
    return {
      pageNum: 1, // 当前页码
      pageSize: 20, // 每页条数
      total: 0, // 数据总数
      loading: false, // 是否加载中
      search: { // 搜索条件
