      },
      pickerOptions: { // 时间筛选快捷选择，今天，最近一周，最近一月
        shortcuts: [
          {
            text: '今天',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '最近一周',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.getTime() - 3600 * 1000 * 24 * 7)
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '最近一个月',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.getTime() - 3600 * 1000 * 24 * 30)
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '最近三个月',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.getTime() - 3600 * 1000 * 24 * 90)
              picker.$emit('pick', [start, end])
            }
          }
        ]
      }
    }
  },
  created() {
    this.queryList && this.queryList() // 请求列表数据
  },
  methods: {
    // 列表渲染格式转换
    formatter(row, column, cellValue, index) {
      if (!cellValue) {
        return '-'
      } else {
        return cellValue
      }
    },
    // 修改页码
    changePage(val) {
      this.pageNum = val
      this.queryList()
    },
    // 修改每页长度
    changeSize(val) {
      this.pageNum = 1
      this.pageSize = val
      this.queryList()
    },
    // 数据操作
    operate(type, row) {
      if (!this.detailPath) {
        this.$message({
          message: '请指定详情页面路径: detailPath',
          type: 'warning'
        })
      }
      switch (type) {
        case 'add':
        case 'update':
        case 'detail':
          this.$router.push({
            path: this.detailPath,
            query: {
              type,
              id: row ? row[this.dataId] : '' // dataId定义在具体list文件中，用以标注列表中表示id的字段key
            }
          })
          break
        case 'delete':
          this.del(row)
          break
        default:
          break
      }
    },
    // 删除
    del(row) {
      this.$confirm('确定删除', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        const delInfo = {}
        delInfo[this.deleteKey] = row[this.dataId]
        if (!this[this.deleteMethod]) {
          this.$message({
            message: `缺少删除接口: ${this.deleteMethod || ''}`,
            type: 'warning'
          })
        }
        this[this.deleteMethod](delInfo).then((res) => { // dataId定义在具体list文件中，用以标注列表中表示id的字段key
          this.$message({
            type: 'success',
            message: '删除成功!'
          })
          this.queryList()
        }).catch((err) => {
          console.error(err)
        })
      })
    },
    // 打开高级搜索
