      if (document.querySelector('.zl-search .more i').className === 'el-icon-arrow-down') {
        document.querySelector('.zl-search').style.height = 'auto'
        document.querySelector('.zl-search .more i').className = 'el-icon-arrow-up'
      } else {
        document.querySelector('.zl-search').style.height = '30px'
        document.querySelector('.zl-search .more i').className = 'el-icon-arrow-down'
      }
    },
    back() {
      this.$router.go(-1)
    }
  }
}

