    let reuslt = await api.wxChooseImage({
        count: 1,
        sizeType: ['original', 'compressed'],
        sourceType: ['album', 'camera']
    })
    const file = reuslt.tempFiles[0]
    var index = file.path.lastIndexOf(".");
    var suffix = file.path.substr(index + 1)
    let res = await api.uploadFile(file.path, suffix)
    res = JSON.parse(res)
    if (res.code === 200) {
        return {
            localPic: file.path,
            url: res.msg
        }
    } else {
        util.showMsg('图片上传有错误,请重试')
    }
  }"}]
